import { createSlice } from '@reduxjs/toolkit'

export const carItemSlicer = createSlice({
    name: "car",
    initialState: { value: { car: [] } },
    reducers: {
        addCar: (state, action) => {
            console.log(action.payload);
            state.value = { car: action.payload }
        },
    },
})
export const { addCar } = carItemSlicer.actions
export default carItemSlicer.reducer
